import React, {useContext} from "react"
import {cartContext} from "../Global/cartContext"
import {Link} from "react-router-dom"
import logo from "../assets/gnglogo.svg"

const Navbar = ({cartToggle}) => {
   const {shoppingCart} = useContext(cartContext);
    return(
        <nav class="navbar navbar-light bg-light justify-content-between">
            <a class="navbar-brand"> <Link to="/"><img src={logo} /></Link>
</a>

 <Link to="/"><img src={logo} /></Link>
 {/* <li><form>
     <input type="text" className="search" placeholder="Search Product..."/>
     </form></li> */}
<form class="form-inline">
  <li class="form-control mr-sm-2" onClick={cartToggle}><Link to="/cart"><span className="dollor"><i className="fas fa-cart-plus"></i></span><span className="shoppingCartTotal">{shoppingCart ? shoppingCart.length : 0}</span></Link></li>

  </form>
</nav>
    )

}

export default Navbar;
