import React, {useContext} from "react"
import { productContext } from "../Global/productContext"
import {cartContext} from "../Global/cartContext"
import { Link } from 'react-router-dom';

import Banner from "./Banner"
const Recommendation = () => {
   const {recommendation_product} = useContext(productContext);
   const {dispatch} = useContext(cartContext);
    return(
        <>
     <div className="container-fluid text-center mt-5"> <h2>You Might Also Like</h2></div>
        <div className="products" style={{marginTop: '30px'}}>
        {recommendation_product && recommendation_product.map(rc_product => (
            <div className="product" key={rc_product.id}>
              <div className="pro">
            <div className="productImage">
              <img src={rc_product.image} alt="Not found"/>
            </div>
            <div className="productDetails">
                <div className="proName">
                 <h4>{rc_product.name}</h4>
                </div>
               <div className="proPrice">
                   ${rc_product.price}.00
               </div>
            </div>
          <div className="proButton" onClick={() => dispatch({type: 'ADD_TO_CART', id: rc_product.id, recommendation_product,rccategory:true})}>
                 <button className="buyNow" >add to cart</button>
               </div>
               {rc_product.productStatus === 'new' ? <div className="new">New</div>: ''}
               {rc_product.productStatus === 'hot' ? <div className="hot">Hot</div>: ''}
            </div>
            </div>
        ))}

        </div>

        </>
    )

}

export default Recommendation;
